//날짜를 [yyyy-mm-dd]형식으로 만들어주기
export const getStringDate = (date) => {
    /*
    let year = date.getFullYear();
    let month = date.getMonth() + 1;
    let day = date.getDate();
    if (month < 10) {
        month = `0${month}`;
    }
    if (day < 10) {
        day = `0${day}`;
    }
    return `${year}-${month}-${day}`;*/

    return date.toISOString().slice(0, 10);
};
