import { useState, useContext, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { DiaryStateContext } from "../App";
import DiaryEditor from "../components/DiaryEditor";

const Edit = () => {
    const [originData, setOriginData] = useState();
    const navigate = useNavigate();
    const {id} = useParams(); //id꺼내주기
    const diaryList = useContext(DiaryStateContext);

    useEffect(() => {
        const titleElement = document.getElementsByTagName("title")[0];
        titleElement.innerHTML = `Emotion Diary - Edit Diary ${id}`;
      }, []);

    //id나 리스트가 변할 때만 컨텐츠 불러오기
    useEffect(()=>{
        if(diaryList.length >= 1){
            const targetDiary = diaryList.find(
                (it) => parseInt(it.id) === parseInt(id)
            ); //it.id는 아이템의 아이디 id는 useParams으로 가져온것
            if(targetDiary){
                setOriginData(targetDiary);
            }else{ //undefined나 리스트 없는 숫자가 주소창에 붙을 경우
                alert("Invalid path");
                navigate('/', {replace:true}); //뒤로도 못가게
            }
        }
    },[diaryList, id]);

    //오리지널 데이터가 있으면 DiaryEditor를 불러오기
    return(
        <div>
            {originData && <DiaryEditor isEdit={true} originData={originData}/>}
        </div>
    );
};
export default Edit;
