import { useState, useRef, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { DiaryDispatchContext } from "./../App"

import MyHeader from "./MyHeader";
import MyButton from "./MyButton";
import EmotionItem from "./EmotionItem";


//Q1. '오늘'의 날짜를 초기값으로 넣어주기 위해서 
const getStringDate = (date) => {
    //[yyyy-mm-dd]형식
    let year = date.getFullYear();
    let month = date.getMonth() + 1;
    let day = date.getDate();
    if (month < 10) {
        month = `0${month}`;
    }
    if (day < 10) {
        day = `0${day}`;
    }
    return `${year}-${month}-${day}`;
}

//Q2. 감정처리
const emotionList = [
    {
        emotion_id:1,
        emotion_img: process.env.PUBLIC_URL + `/assets/emotion1.png`,
        emotion_descript: "So Good"
    },
    {
        emotion_id:2,
        emotion_img: process.env.PUBLIC_URL + `/assets/emotion2.png`,
        emotion_descript: "Good"
    },
    {
        emotion_id:3,
        emotion_img: process.env.PUBLIC_URL + `/assets/emotion3.png`,
        emotion_descript: "So so"
    },
    {
        emotion_id:4,
        emotion_img: process.env.PUBLIC_URL + `/assets/emotion4.png`,
        emotion_descript: "Bad"
    },
    {
        emotion_id:5,
        emotion_img: process.env.PUBLIC_URL + `/assets/emotion5.png`,
        emotion_descript: "So Bad"
    },
]


const DiaryEditor = () => {

    //1.작성날짜 지정
    const [date, setDate] = useState(getStringDate(new Date()));
    const navigate = useNavigate();

    //2.선택 감정 저장
    const [emotion, setEmotion] = useState(3);
    //클릭한 감정을 prop으로 전달
    //EmotionItem에 onClick으로 받아온 id를 가져와
    //현재 상대로 바꿔줌.
    const handleClickEmote = (emotion) => {
        setEmotion(emotion);
    }

    //3.작성폼
    //작성하는 내용 바로 반영해주기
    const [content, setContent] = useState("");
    //빈칸이면 포커스 가도록
    const contentRef = useRef();  
    //4. 저장
    const {onCreate} = useContext(DiaryDispatchContext);
    const handleSubmit = () => {
        if(content.length < 1){
            contentRef.current.focus();
            return;
        }
        onCreate(date,content,emotion); //생성하고
        navigate('/', {replace:true}); //뒤로가고 못돌아오게
    }



    return(
        <div className="">
            {/* Header */}
            <MyHeader 
            headText={"Write new diary"}
            leftchild={
                <MyButton 
                text={"< Back"}
                onClick={() => navigate(-1)}
                />
                }
            />
            <div className="DiaryEditor">
                {/* Q1 */}
                <section>
                    <h4>What is the date today?</h4>
                    <div className="input_box">
                        <input 
                        className="input_date"
                        value={date} 
                        type="date" 
                        onChange={(e)=>setDate(e.target.value)}
                        />
                    </div>
                </section>
                {/* Q2 */}
                <section>
                    <h4>Today's feeling</h4>
                    <div className="input_box emotion_list_wrapper">
                        {emotionList.map((it) => (
                            <EmotionItem 
                            key={it.emotion_id} {...it} 
                            onClick={handleClickEmote}
                            isSelected={it.emotion_id === emotion}
                            />
                        ))}
                    </div>
                </section>
                {/* Q3 */}
                <section>
                    <h4>Today's Diary</h4>
                    <div className="input_box text_wrapper">
                        <textarea 
                        placeholder="How was your day?"
                        ref={contentRef} value={content}
                        onChange={(e) => setContent(e.target.value)}/>
                    </div>
                </section>
                {/* Q4 */}
                <section>
                    <div className="control_box">
                        <MyButton text={'Cancel'} 
                        onClick={()=>navigate(-1)}/>
                        <MyButton text={'Save'} 
                        type={'positive'} onClick={handleSubmit}/>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default DiaryEditor;
