//각각 나열된 감정 중에 뭐를 눌렀는지 알아내기 위해
const EmotionItem = ({
    emotion_id, 
    emotion_img, 
    emotion_descript, 
    onClick,
    isSelected,
}) => {
    return(
        <div onClick={()=>onClick(emotion_id)}
        className={["EmotionItem", isSelected ? `EmotionItem_on_${emotion_id}` : `EmotionItem_off`].join(" ")}

        >
            <img src={emotion_img} />
            <span>{emotion_descript}</span>
        </div>
    );
};

export default EmotionItem;
